mapper.update:

	update member
	set with_category_id = ${withCategoryId},with_reason=#{withContent}
	where member_id = ${memberId}

update shop
address = #{address}, phone = #{phone}, intro_ph = #{intro}, holiday = #{holiday}, start_date = #{startDate}, end_date = #{endDate}, homepage_url = #{homepageUrl}, to_benefit = #{toBenefit}, notice_info = #{noticeInfo}
where shop_idx = ${shopIdx}

mapper.select:

	select goods_id,name,price,min_price,additional_info,best
	from goods 
	where shop_idx = ${shopIdx}


select name,price,min_price,additional_info,best
from goods
where goods_id = ${goodsId}

mapper.update:
	
	update goods
	set name=#{name},best=${best},price=#{price},
	min_price=#{minPrice},additional_info=#{additionalInfo}
	where goods_id = ${goodsId}

mapper.select:

	select image_name
	from shop_image
	where shop_idx = ${shopIdx}


select keyword
from shop_keyword
where shop_idx = ${shopIdx}

	
